sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
		if ( newValue == '') {
			return;
		}

		var whatneeded = g_form.getValue('u_choice_4');

		// Clears the choices from the what needed list
		g_form.clearOptions('u_choice_4');

		//If value of the Request Type field = HR, add 2 'HR'choices, and 'other' to the 
		// choice list

		if(newValue == 'hr'){
			g_form.addOption('u_choice_4','hr1','Human Resources 1');
			g_form.addOption('u_choice_4','hr2','Human Resources 2');
			g_form.addOption('u_choice_4','other','Other');
		}

			// If value of the Request type field = facilities, add 2 facilities choices and other 
			// to What needed field choice list
		if(newValue == 'facilities'){
			g_form.addOption('u_choice_4','facilities1','Facilities 1');
			g_form.addOption('u_choice_4','facilities2','Facilities 2');
			g_form.addOption('u_choice_4','other','Other');
		}
			// If value of the Request type field = 'legal', add 2 legal choices and other to 
			// what needed field choice list

		if(newValue == 'legal'){
			g_form.addOption('u_choice_4','legal1','Legal 1');
			g_form.addOption('u_choice_4','legal2','Legal 2');
			g_form.addOption('u_choice_4','other','Other');
		}

		// If form is loading and is not new record, set the u_choice_4 value to the
		// value from the record before it was loaded
		if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('u_choice_4', whatneeded);
		}
	}
